var usage = {};
for (var model_name in cms.model_data['model']) {
    var model = cms.model_data['model'][model_name];
    model.fields.forEach(function(field) {

        if (field.type == 'Widgets') {
            for (var record in cms.model_data[model_name]) {
                var data = cms.model_data[model_name][record];

                if (data[field.name]) {
                    for (var id in data[field.name].widgets) {
                        var widget = data[field.name].widgets[id];

                        usage[widget.type] = usage[widget.type] || 0;
                        usage[widget.type]++;
                    }
                }
            }
        }
    });
}
return usage;
