var widgetValues = {};

_.each(values, function(value, key) {
    var parts = key.split("-");
    if (parts.length == 2) {
        var widget_id = parts[0];
        var value_id = parts[1];

        if (!widgetValues[widget_id]) {
            widgetValues[widget_id] = {};
        }

        widgetValues[widget_id][value_id] = value;
    }
});

return widgetValues;
