var search = angular.module('search',[]);
search.controller('SearchController', ['$scope', function($scope) {
  $scope.greeting = 'Hola!';
}]);

window['SearchController'] = function ($scope) {
  this.greeting = 'Hola123!';
  this.query = '';

  this.search = function() {
    this.results = nw.indexes.model.search(this.query);
    this.results = this.results.map(function(entry) {
      return nw.indexes.modelTerms[entry.ref];
    });
    console.log(this.results);
  }

  Mousetrap.bind('enter', function(e) {
    if (nw.inSearch) {
      window.location = this.results[0].href;
    }
    return false;
  }.bind(this));
}

function updateIndices(ctrl) {
  nw.indexes = {};  
  nw.indexes.model = lunr(function () {
     this.field('name')
  });
  nw.indexes.modelTerms = {};
  Object.keys(nw.data).forEach(function(model) {
    var key = 'list-'+model;
    var name = 'list ' + model;
    nw.indexes.modelTerms[key] = {
      href: '/admin/list/' + model,
      text: name,
    };
    nw.indexes.model.add({id: key, name: name});

    var key = 'edit-'+model;
    var name = 'edit ' + model;
    nw.indexes.modelTerms[key] = {
      href: '/admin/data/?model=model&record=' + model,
      text: name,
    };
    nw.indexes.model.add({id: key, name: name});

    var key = 'add-'+model;
    var name = 'add ' + model;
    nw.indexes.modelTerms[key] = {
      href: '/admin/data/?model='+model+'&record=create',
      text: name,
    };
    nw.indexes.model.add({id: key, name: name});
  });
}

nw.inSearch = false;
Mousetrap.bind('ctrl+enter', function(e) {
  $('.widget-admin_search').toggle();
  nw.inSearch = !nw.inSearch;
  $('.widget-admin_search input').focus();
  if (!nw.data) {
    $.getJSON("/internal/all_data", function(result) {
      nw.data = result;
      updateIndices(this);
    });
  }
  return false;
});
Mousetrap.bind('escape', function(e) {
  if (nw.inSearch) {
    nw.inSearch = false;
    $('.widget-admin_search').hide();
  }
  return false;
});
