  height: 80%;
  background: white;
  border: 1px solid black;
  border-radius: 15px;
  box-shadow: 0px 0px 10px #aaa;
  padding: 2px 15px 2px 15px;
  z-index: 10;
}

.widget-admin_search .list-group-item {
  padding: 5px 15px;
}
