toHTML.javascript:
var element = '<div class="json" style="width: ' + (this.settings.width ? this.settings.width + 'px' : 'auto') + '; height: ' + (this.settings.height || 200) + 'px;"></div>';

if (this.settings.inline) {
    return '<div class="controls form-inline">' + (this.settings.label ? label : '') + element + '</div>';
} else {
    return (this.settings.label ? label : '') + element;
}

action.javascript:
var container = document.querySelector('#' + id + ' .json');
var options = {
    mode: 'code',
    modes: ['code', 'tree'], // allowed modes
};
var data = settings.data || {};
data = typeof data == 'string' ? JSON.parse(data) : data;
var editor = new JSONEditor(container, options, data);

return {
    get: function() {
        return JSON.stringify(editor.get());
    }
}
