toHTML.javascript:
var html = '<table class="tablesorter-blue">';
html += '<thead> <tr> <th>Module</th> <th>Name</th> <th>Priority</th> </tr> </thead>'

html += '<tbody>';
_.each(cms.middleware, function(w) {
    html += '<tr> <td>' + w.module + '</td> <td>' + w.name + '</td> <td>' + w.priority + '</td> ';
    html += '</tr>';
});
html += '</tbody>';

html += '</table>';

return html;
