toHTML.javascript:
var row_template;
if (this.settings.row_template) {
    row_template = Handlebars.compile(this.settings.row_template);
}

var html = '';

html += '<h1>' + (this.settings.title ? this.settings.title : ('<a href="/admin/list/model">Models</a> : ' + this.settings.model)) + '</h1>';

if (this.settings.add_button) {
    html += '<a class="btn btn-primary" href="/admin/data/edit?model=' + this.settings.model + '&record=create">Create new</a></li>';
}

if (!this.settings.manual_list) {
    html += '<ul class="list-group">'
}

var data = cms.model_data[this.settings.model];
for (key in data) {
    var list = data[key];
    if (!this.settings.manual_list) {
        html += '<li class="list-group-item" >';
    }
    if (!this.settings.row_template) {
        html += '<a href="/admin/data/edit?model=' + this.settings.model + '&record=' + key + '">' + key + '</a>';
    } else {
        list['key'] = key;
        html += row_template(list);
    }
    if (!this.settings.manual_list) {
        html += '</li>';
    }
}

if (!this.settings.manual_list) {
    html += '</ul>';
}

if (this.settings.add_button) {
    html += '<a class="btn btn-primary" href="/admin/data/?model=' + this.settings.model + '&record=create">Create new</a></li>';
}

return html;
