cms.functions.getRecord(this.settings.model, this.settings.record, function(err, data) {
    var record = data;
    cms.functions.getRecord('model', this.settings.model, function(err, data2) {
        var model = data2;
        var model_view = model.views[this.settings.view];
        console.log(model.views);
        _.each(model_view.widgets, function(widget) {
            if (widget.model && widget.field) {
              var field = cms.functions.getModelField(model, widget.field);
                widget.settings = field.settings || {};
              	widget.settings.quantity = field.quantity;
                widget.type = cms.functions.getWidget(widget.type, widget.settings);
                widget.settings.data = record[widget.field];
                widget.settings.label = widget.field;
              if (widget.model_type) {
                widget.settings.model = 'model';
                widget.settings.record = widget.model_type;
                widget.settings.inline = 'multi';
              }
            }
        }.bind(this));
        callback({
            'body': model_view.widgets
        }, model_view.slotAssignments);
    }.bind(this));
}.bind(this));
