setup.javascript:
this.settings.schema = _.cloneDeep(this.settings.schema);
var fields = this.settings.schema.fields;
this.settings.fields = fields;
_.each(this.settings.schema.display.widgets, function(widget, id) {
  //console.log(widget);
  if (widget.field && fields[widget.field] ) {
    widget.settings.data = this.settings.data[widget.field];
    //console.log(fields[widget.field]);
    if (fields[widget.field].quantity) {
      widget.settings.quantity = fields[widget.field].quantity;
    }
    //fields[widget.field].widget = widget.type;
    widget.type = cms.functions.getWidget(widget.type, widget.settings, widget);
    fields[widget.field].widget = widget.type;
    fields[widget.field].settings = widget.settings;
  }
}.bind(this));
