htmlTemplate.htmlmixed:
{{{label}}}
<select class="sel form-control" {{#if settings.quantity}}multiple{{/if}} name="{{id}}">
    {{#unless settings.quantity}}
    <option value="">- Select -</option>
    {{/unless}} {{#each choicesCombined}}
    <option value="{{this.[0]}}" {{this.[2]}}>{{this.[1]}}</option>
    {{/each}}
</select>

setup.javascript:
this.settings = cms.functions.evalFunctions('select', this.settings);
var choices = this.settings.choices;
var data = this.settings.data;
if (typeof choices == 'function') {
  choices = choices();
}
if (Array.isArray(choices)) {
    choices = _.object(choices, choices);
}
this.choicesCombined = [];
_.each(choices, function(choice, key) {
  if ( (this.settings.quantity && _.contains(data, choice)) || 
       (!this.settings.quantity && data == choice) )
    this.choicesCombined.push([key, choice, 'selected']);
  else
    this.choicesCombined.push([key, choice, '']);
}.bind(this));
