setup.javascript:
var cols = [];

this.settings.data.forEach(function(row) {
  cols = _.union(cols, Object.keys(row));
});
this.cols = cols;

var data = [];
this.settings.data.forEach(function(row) {
  var buffer = [];
  this.cols.forEach(function(col) {
    buffer.push(row[col]);
  });
  data.push(buffer);
}.bind(this));
this.data = data;
Handlebars.registerHelper('json', function(context) {
    return JSON.stringify(context);
});
