action.javascript:
var data = {
    email: settings.email,
    password: settings.password
};
nw.functions.doProcess(settings.token, {
    data: data
}, function(result) {
    handlers.success();
}, handlers.failure);

doProcess.javascript:
var cookies = new Cookies(this.req, this.res, COOKIE_KEYS);
cms.functions.findOneByField('user', 'email', input.data.email, function(err, data) {
    if (err) {
        callback('invalid');
        return
    }
    if (data.password == input.data.password) {
        cookies.set('clientID', data.clientID, {
            signed: true,
            overwrite: true
        });
        console.log(data.clientID + ' logged in');
        callback(undefined, data);
        return;
    } else {
        callback('invalid p');
    }
});
