toHTML.javascript:
var html = '<div class="vertical-tabs-panes">';
console.log(this.settings);
for (var index in this.slotAssignments.tabs) {
  var widget = this.slotAssignments.tabs[index];
  html += '<fieldset class="menu-link-form collapsible collapsed">';
  html +=  '<legend><span class="fieldset-legend">'+this.settings.tabs[index]+'</span></legend>';
  html += widget.html();
  html += '<div class="fieldset-wrapper"></div></fieldset>';
}
html += '<input class="vertical-tabs-active-tab" type="hidden" name="additional_settings__active_tab" value="" /></div>';
return html;

style.css:
/**
 * Markup free clearing.
 *
 * @see http://perishablepress.com/press/2009/12/06/new-clearfix-hack
 */
.clearfix:after {
  content: ".";
  display: block;
  height: 0;
  clear: both;
  visibility: hidden;
}
/* IE6 */
* html .clearfix {
  height: 1%;
}
/* IE7 */
*:first-child + html .clearfix {
  min-height: 1%;
}

.widget-vertical_tabs a {
  color: #0074BD;
  text-decoration: none;
}
